sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue === '') {
        return;
    }

    var groupId = g_form.getValue('group_assigned'); // Replace 'group_assigned' with your actual group field name
    if (groupId) {
        var ga = new GlideAjax('GetGroupMembers');
        ga.addParam('sysparm_name', 'getMembers');
        ga.addParam('sysparm_group_id', groupId);
        ga.getXMLAnswer(function(response) {
            var userIds = response.split(',');
            g_form.setValue('assigned_person', ''); // Clear the field
            g_form.getControl('assigned_person').options.length = 0; // Clear existing options
            userIds.forEach(function(userId) {
                g_form.addOption('assigned_person', userId, userId); // Add new options
            });
        });
    }
}
